import styled from 'styled-components';

export const HomeContainer = styled.div`
	min-width:1000px;
	width: 65%;
	margin: 30px auto;
	overflow:hidden;
`;

export const LeftContainer = styled.div`
	float:left;
	width:62%;
`;

export const RightContainer = styled.div`
	float: right;
	width:30%;
	height:400px;
	background:green;
`;

//Banner
export const BannerWrapper = styled.div`
	width:625px;
	height:270px;
	border-radius:6px;
	overflow:hidden;
	margin:0 auto;
	margin-bottom: 30px;
	position:relative;
	.imgWrapper{
		position: absolute;
		left: 0px;
		width: 3125px;
		height: 100%;
		transition: left 1s ease-in-out;
		.bannerImg{
			width:625px;
			height:270px;
			float: left;
		}
	}
	.iconLeft{
		opacity: 0;
		position: absolute;
		top: 40%;
		width: 40px;
		height: 50px;
		font-size: 20px;
		color: #fff;
		box-shadow: 0 1px 2px rgba(0,0,0,.6);
		line-height: 50px;
		text-align: center;
		background:rgba(0,0,0,0.4);
		border-top-left-radius: 6px;
		border-bottom-left-radius: 6px;
		transform-origin: center center;
		transform: rotate(180deg);
		cursor: pointer;
		display: block;
		
	}
	.iconRight{
		opacity: 0;
		position: absolute;
		top: 40%;
		right: 0;
		width: 40px;
		height: 50px;
		font-size: 20px;
		color: #fff;
		box-shadow: 0 1px 2px rgba(0,0,0,.6);
		line-height: 50px;
		text-align: center;
		background:rgba(0,0,0,0.4);
		border-top-left-radius: 6px;
		border-bottom-left-radius: 6px;
		cursor: pointer;
		display: block;
	}
`;

//homeList

export const ContentListWrapper=styled.ul`
	.listItem{
		width: 100%;
		height: 104px;
		border-bottom: 1px solid #f0f0f0;
		.itemImg{
			width: 150px;
			height: 100px;
			
			float: right;
		}
		.content{
			box-sizing: border-box;
			display: block;
			float:left;
			padding-right: 165px;
			.listHerf{
				text-decoration: none;
				color:#333;
				font-size: 18px;	
				font-weight: 700;
				line-height: 1.5px;		
			}
		}
	}
`;
