import {fromJS} from 'immutable';
import {actionTypes} from 'index';
const defaultState = fromJS({
	login:true
});

export default (state=defaultState, action) => {
	switch(action.type) {
		case actionTypes.CHANGE_LOGIN:
			return state.set('login', action.value);
	}
	return state;
}
