import styled from 'styled-components';
import LogoImg from '../../static/logo.png';
import Beta from '../../static/beta.png';

export const HeaderWrapper = styled.header`
	//p{
		display:flex;
		border-bottom: 1px solid #eee;
		min-width: 1060px;
		flex-flow: row wrap;
		height: 56px;
		box-sizing: border-box;

	//}
		
`;

export const LogoPic = styled.a`
	flex: 1;
	display: block;
	cursor: pointer;
	background: url(${LogoImg});
	background-size: contain;
	background-repeat: no-repeat;
`;

export const HeaderContainer = styled.nav`
	display: flex;
	flex-flow: row;
	align-items: center;
	&.leftCon{
		flex: 4;
		justify-content: flex-start;
		padding: 0 20px;
		display: flex;
		.homePage{
			flex:1;
		}
		.downloadAPP{
			flex: 1.5;
		}
	}
	&.rightCon{
		flex: 2;
		justify-content: flex-end;
		padding: 0 20px;
	}
	.iconfont{
		margin-left: -30px;
		height: 26px;
		width: 26px;
		border-radius: 13px;		
		line-height: 26px;
		text-align: center;
		&.focusedBox{
			background-color: #666;
		color:#fff;
		}
	}
`;

export const HeaderItem = styled.a`
 //p{
 	text-decoration: none;
	font-size:17px;
	line-height:26px;
	padding: 15px;
	cursor: pointer;
	&.homePage{
  	color: #ea6f5a;
	}
	&.downloadAPP{
  	color: #333;
	}
	&.beta{
		width:60px;
		background: url(${Beta});
		background-size: contain;
		background-repeat: no-repeat;
	}
	&.login{
		font-size:15px;
		color: #969696;
	}
//}
`;

export const SearchBox = styled.input`
	//p{
		height: 38px;
		border:1px solid #eee;
		background: #eee;
		font-size: 14px;
		border-radius: 19px;
		outline: none;
		padding: 0 35px 0 15px;
		box-sizing: border-box;
		&.sbox-enter{
			padding-right: 35px;
		}
		&.sbox-enter-active{
			padding-right: 50px;
			transition: all 0.5s ease-in-out;
		}
		&.sbox-enter-done{
			padding-right: 50px;
			transition: all 0.5s ease-in-out;
		}
		&.sbox-exit{
			padding-right: 50px;
		}
		&.sbox-exit-active{
			padding-right: 35px;
			transition: all 0.5s ease-in-out;
		}
		&.sbox-exit-done{
			padding-right: 35px;
			transition: all 0.5s ease-in-out;
		}

//}
`;

export const HotSearchArea =styled.div`
	//p{
		margin-top: 58px;
	width: 100%;
	height: 200px;
	background-color: red;
	//}
`;

export const HotSearchItem = styled.a`
	//p{
	
	//}
`;
export const Additional = styled.div`
	flex: 1.5;
	display: flex;
	align-items: center;
`;

export const Button = styled.a`
	//p{
		flex:1;
		border: 1px solid #ea6f5a;
		font-size:16px;
		height: 38px;
		line-height: 26px;
		box-sizing: border-box;
		margin: 0 10px;
		border-radius: 19px;
		cursor: pointer;
		&.reg{
			color:#ea6f5a;
			padding: 6px;
			text-align: center;
		}
		&.writeArticles{
			color:#fff;
			background: #ea6f5a;
			padding: 6px 12px;
			text-align: center;
		}
	//}
`;
