const defaultState = {
	focused: false,
	hello: 'test'
}

//导出一个纯函数
export default (state = defaultState, action) => {
	if(action.type === 'box_focus') {
		console.log(state);
		return {
			focused: true
		}
	}else if(action.type === 'box_blur') {
		console.log(state);
		return {
			focused: false
		}
	}
	return state;
}
