import {actionTypes} from './index';
import {fromJS} from 'immutable';
//fromJS 也会把list数组编程immutable对象，所以传进来的action里的数据也需要预先转换为immutable对象
const defaultState = fromJS({
	focused: false,
	list:[],
	searchMouseIn: false
});

//导出一个纯函数,运用了immutable之后，返回的就为一个immutable对象，
//也就是在别的文件里面引用state.header就为一个immutable对象
export default (state = defaultState, action) => {
	if(action.type === actionTypes.BOX_FOCUS) {
		//返回的为一个依据之前state以及set的数据新生成的一个immutable对象
		return state.set('focused', true);
	}else if(action.type === actionTypes.BOX_BLUR) {
		return state.set('focused', false);
	}else if(action.type === actionTypes.GET_SEARCH_LIST){
		return state.set('list', fromJS(action.list));
	}
	return state;
}
