import React from 'react';
import {connect} from 'react-redux';
import {createActions} from './headerStore';
import {CSSTransition} from 'react-transition-group';
import '../../static/iconfont/Icon.css';
import {
	HeaderWrapper,
	LogoPic,
	HeaderContainer,
	Additional,
	HeaderItem,
	SearchBox,
	Button,
	HotSearchArea
}from './style.js'

function Header(props) {
	return (
		<HeaderWrapper>
			<LogoPic />
			<HeaderContainer className = 'leftCon'>
				<HeaderItem className = 'homePage' href='/'>首页</HeaderItem>
				<HeaderItem className = 'downloadAPP'>下载APP</HeaderItem>
				<CSSTransition
					in = {props.focused}
					timeout = {200}
					classNames = 'sbox'
				>
					<SearchBox placeholder='搜索' 
						onFocus = {props.handleBoxFocus}
						onBlur = {props.handleBoxBlur}
					>
						<div className='hotSearchArea'>
							<p className='hotSearch'>热门搜索</p>
							<p className='changeOne'>换一批</p>
							<HotSearchArea>简书</HotSearchArea>
							<HotSearchArea>React</HotSearchArea>
							<HotSearchArea>简书</HotSearchArea>
							<HotSearchArea>区块链</HotSearchArea>
							<HotSearchArea>简书</HotSearchArea>
							<HotSearchArea>简书</HotSearchArea>
							<HotSearchArea>小程序</HotSearchArea>
							<HotSearchArea>简书</HotSearchArea>
						</div>
					</SearchBox>					
				</CSSTransition>
				<span className = {props.focused ? 'focusedBox iconfont': 'iconfont'}>&#xe60c;</span>
			</HeaderContainer>
			<HeaderContainer className = 'rightCon'>
				<HeaderItem className = 'aa'><span className="iconfont">&#xe636;</span></HeaderItem>
				<HeaderItem className = 'beta'></HeaderItem>
				<HeaderItem className = 'login'>登录</HeaderItem>
			</HeaderContainer>
			<Additional>
				<Button className = 'reg'>注册</Button>
				<Button className = 'writeArticles'><span className="iconfont">&#xe6a4;</span>写文章</Button>
			</Additional>
		</HeaderWrapper>
	)
}



const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleBoxFocus() {		
			dispatch(createActions.handleBoxFocusAction());
		},

		handleBoxBlur() {
			dispatch(createActions.handleBoxBlurAction());
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Header);
