import React, {Component} from 'react';
import {connect} from 'react-redux'; 
import {
	BannerWrapper
}from '../style.js'

class Banner extends Component {
	constructor(props) {
		super(props);

		this.wrapper = React.createRef();
	}
	render() {
		return (
			<BannerWrapper>
				<div className='imgWrapper' ref={this.wrapper}>
					<img className = 'bannerImg' src='../images/banner1.jpg' alt='bannerImage'/>
					<img className = 'bannerImg' src='../images/banner2.png' alt='bannerImage'/>
				</div>
			</BannerWrapper> 
		)
	}

	componentDidMount() {
		this.props.bannerSlide(this.wrapper);
	}
}

