import React, {Component} from 'react';
import {CSSTransition} from 'react-transition-group';
import '../../static/iconfont/Icon.css';
import {
	HeaderWrapper,
	LogoPic,
	HeaderContainer,
	Additional,
	HeaderItem,
	SearchBox,
	Button
}from './style.js'

class Header extends Component {

	constructor(props) {
		super(props);

		this.state = {
			focused: false
		}
		this.handleBoxFocus= this.handleBoxFocus.bind(this);
		this.handleBoxBlur = this.handleBoxBlur.bind(this);
	}
	render() {
		return (
			<HeaderWrapper>
				<LogoPic />
				<HeaderContainer className = 'leftCon'>
					<HeaderItem className = 'homePage' href='/'>首页</HeaderItem>
					<HeaderItem className = 'downloadAPP'>下载APP</HeaderItem>
					<CSSTransition
						in = {this.state.focused}
						timeout = {200}
						classNames = 'sbox'
					>
						<SearchBox placeholder='搜索' 
							className = {this.state.focused ? 'focusedBox': ''}
							onFocus = {this.handleBoxFocus}
							onBlur = {this.handleBoxBlur}
						/>					
					</CSSTransition>
					<span className="iconfont">&#xe60c;</span>
				</HeaderContainer>
				<HeaderContainer className = 'rightCon'>
					<HeaderItem className = 'aa'><span className="iconfont">&#xe636;</span></HeaderItem>
					<HeaderItem className = 'beta'></HeaderItem>
					<HeaderItem className = 'login'>登录</HeaderItem>
				</HeaderContainer>
				<Additional>
					<Button className = 'reg'>注册</Button>
					<Button className = 'writeArticles'><span className="iconfont">&#xe6a4;</span>写文章</Button>
				</Additional>
			</HeaderWrapper>
		)
	}

	handleBoxFocus() {
		this.setState(()=>({
			focused: true
		}))
	}

	handleBoxBlur() {
		this.setState(() => ({
			focused: false
		}))
	}
}

export default Header;
