import * as actionTypes from './actionTypes';
import axios from 'axios';

function getArticle(title, content){
	return ({
		type: actionTypes.GET_ARTICLE,
		title,
		content
	});
}

export const initArticleAction = () => {
	return axios.get('./article.json').then((res) => {
		const result = res.data.data;
		return (dispatch)=>{
			console.log(getArticle(result.title, result.content));
			dispatch(getArticle(result.title, result.content));
		}
	})
}
