import React, {Component} from 'react';
import {connect} from 'react-redux'; 
import {
	BannerWrapper
}from '../style.js'

class Banner extends Component {
	constructor(props) {
		super(props);

		this.wrapper = React.createRef();
	}
	render() {
		return (
			<BannerWrapper 
				onMouseEnter={this.props.hoverSlide} 
				onMouseLeave = {()=>{this.props.blurSlide(this.wrapper)}}
			>				
				<div className='imgWrapper' ref={this.wrapper}>
					<img className = 'bannerImg' src='../images/banner1.jpg' alt='bannerImage'/>
					<img className = 'bannerImg' src='../images/banner2.png' alt='bannerImage'/>
					<img className = 'bannerImg' src='../images/banner3.png' alt='bannerImage'/>
					<img className = 'bannerImg' src='../images/banner4.png' alt='bannerImage'/>
					<img className = 'bannerImg' src='../images/banner5.png' alt='bannerImage'/>
				</div>
				<span className='iconfont iconLeft'>&#xe616;</span>
				<span className='iconfont iconRight'>&#xe616;</span>
			</BannerWrapper> 
		)
	}

	componentDidMount() {
		this.props.bannerSlide(this.wrapper);
	}
}

const mapStateToProps = (state) => {
	return {

	}
}
const mapDispatchToProps = (dispatch) => {
	var timer = null;
	return {
		bannerSlide(wrapper) {
			timer = setInterval(()=>{
				const currentPosition = parseInt(getComputedStyle(wrapper.current,null).getPropertyValue('left'));
				if(currentPosition === (4*-625)){
					wrapper.current.style.left='0px';
				}else{
					wrapper.current.style.left = `${currentPosition-625}px`;
				}
			},5000);
		},
		hoverSlide() {
			clearInterval(timer);
		},
		blurSlide(wrapper) {
			this.bannerSlide(wrapper);
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Banner);
