import React, {Component}from 'react';
import {connect} from 'react-redux';
import {createActions} from './headerStore';
import {CSSTransition} from 'react-transition-group';
import '../../static/iconfont/Icon.css';
import {
	HeaderWrapper,
	LogoPic,
	HeaderContainer,
	Additional,
	HeaderItem,
	SearchBox,
	Button,
	HotSearchArea,
	HotSearchItem
}from './style.js'
class Header extends Component {

	getSearchBox() {
		if(this.props.focused){
			return (
				<HotSearchArea>
					<p className='hotSearch'>热门搜索</p>
					<p className='changeOne'><span className='iconfont'>&#xe61d;</span>换一批</p>
					<div className='clearfix'>
					{
						this.props.list.map(item)
					}
						<HotSearchItem>简书</HotSearchItem>
						<HotSearchItem>React</HotSearchItem>
						<HotSearchItem>简书</HotSearchItem>
						<HotSearchItem>区块链</HotSearchItem>
						<HotSearchItem>简书</HotSearchItem>
						<HotSearchItem>简书</HotSearchItem>
						<HotSearchItem>小程序</HotSearchItem>
						<HotSearchItem>简书</HotSearchItem>
					</div>
				</HotSearchArea>
			)
		}else{
			return null
		}
	}

	render () {
		return (
			<HeaderWrapper>
				<LogoPic />
				<HeaderContainer className = 'leftCon'>
					<HeaderItem className = 'homePage' href='/'>首页</HeaderItem>
					<HeaderItem className = 'downloadAPP'>下载APP</HeaderItem>
					<div className ='searchArea'>
						<CSSTransition
							in = {this.props.focused}
							timeout = {200}
							classNames = 'sbox'
						>
							<SearchBox placeholder='搜索' 
								onFocus = {this.props.handleBoxFocus}
								onBlur = {this.props.handleBoxBlur}
							/>					
						</CSSTransition>
						<span className = {this.props.focused ? 'focusedBox iconfont': 'iconfont'}>&#xe60c;</span>
						{ this.getSearchBox(this.props.focused)}
					</div>

				</HeaderContainer>
				<HeaderContainer className = 'rightCon'>
					<HeaderItem className = 'aa'><span className="iconfont">&#xe636;</span></HeaderItem>
					<HeaderItem className = 'beta'></HeaderItem>
					<HeaderItem className = 'login'>登录</HeaderItem>
				</HeaderContainer>
				<Additional>
					<Button className = 'reg'>注册</Button>
					<Button className = 'writeArticles'><span className="iconfont">&#xe6a4;</span>写文章</Button>
				</Additional>
			</HeaderWrapper>
		)
	}

	componentDidMount() {
		this.props.getSearchList();
	}
}


const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused'),
		list: state.get('header').get('list')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleBoxFocus() {		
			dispatch(createActions.handleBoxFocusAction());
		},

		handleBoxBlur() {
			dispatch(createActions.handleBoxBlurAction());
		},
		getSearchList() {
			dispatch(createActions.getSearchListAction());
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Header);
