import styled from 'styled-components';

export const DetailWrapper = styled.div`
	width:50%;
	min-width:700px;
	margin: 0 auto;
	h3{
		font-size:34px;
		font-weight:bold;
		color:#333;
		line-height:1.4;
		text-align:center;
		margin:50px 0 20px;
	}
`;
export const DetailContent = styled.div`
	img{
		width:100%;
		margin-bottom:20px;
	}
	p{
		font-size:16px;
		color:#2f2f2f;
		margin-bottom: 20px;
		line-height:1.4;
	}
	b{
		font-weight:bold;
	}
`;

export const WriterWrapper = styled.div`
	overflow:hidden;
	img{
		border-radius:50%;
		width:40px;
		width:40px;
		float:left;
		margin-right:5px;
	}
	.addFocus{
		color:#fff;
		text-align: center;
		display: inline-block;
		padding: 2px 4px;
		border-radius:4px;
		font-weight: 400;
		font-size:12px;
		background-color: #42c02e;
		.iconfont{
			font-size:12px;
		}
	}
`;
