const defaultState = {
	focused: false
}

//导出一个纯函数
export default (state = defaultState, action) => {
	if(action.type === 'box_focus') {
		const newState = JSON.parse(JSON.stringify(state));
		newState.focused = true;
		return newState;
	}else if(action.type === 'box_blur') {
		const newState = JSON.parse(JSON.stringify(state));
		newState.focused = false;
		return newState;
	}
	return state;
}
