import React, {Component}from 'react';
import {connect} from 'react-redux';
import {createActions} from './headerStore';
import {CSSTransition} from 'react-transition-group';
import '../../static/iconfont/Icon.css';
import {
	HeaderWrapper,
	LogoPic,
	HeaderContainer,
	Additional,
	HeaderItem,
	SearchBox,
	Button,
	HotSearchArea,
	HotSearchItem
}from './style.js'
class Header extends Component {

	getSearchBox() {
		//解构赋值
		const {focused, list, SearchMouseEnter} = this.props;
		if(focused){
			return (
				<HotSearchArea 
					onMouseEnter = {SearchMouseEnter}
				>
					<p className='hotSearch'>热门搜索</p>
					<p className='changeOne'><span className='iconfont'>&#xe61d;</span>换一批</p>
					<div className='clearfix'>
					{
						list.map((item) => {
							return <HotSearchItem key = {item}>{item}</HotSearchItem>
						})
					}
						
					</div>
				</HotSearchArea>
			)
		}else{
			return null
		}
	}

	render () {
		const {focused, handleBoxFocus, handleBoxBlur} = this.props;
		return (
			<HeaderWrapper>
				<LogoPic />
				<HeaderContainer className = 'leftCon'>
					<HeaderItem className = 'homePage' href='/'>首页</HeaderItem>
					<HeaderItem className = 'downloadAPP'>下载APP</HeaderItem>
					<div className ='searchArea'>
						<CSSTransition
							in = {focused}
							timeout = {200}
							classNames = 'sbox'
						>
							<SearchBox placeholder='搜索' 
								onFocus = {handleBoxFocus}
								onBlur = {handleBoxBlur}
							/>					
						</CSSTransition>
						<span className = {focused ? 'focusedBox iconfont': 'iconfont'}>&#xe60c;</span>
						{ this.getSearchBox()}
					</div>

				</HeaderContainer>
				<HeaderContainer className = 'rightCon'>
					<HeaderItem className = 'aa'><span className="iconfont">&#xe636;</span></HeaderItem>
					<HeaderItem className = 'beta'></HeaderItem>
					<HeaderItem className = 'login'>登录</HeaderItem>
				</HeaderContainer>
				<Additional>
					<Button className = 'reg'>注册</Button>
					<Button className = 'writeArticles'><span className="iconfont">&#xe6a4;</span>写文章</Button>
				</Additional>
			</HeaderWrapper>
		)
	}

	componentDidMount() {
		this.props.getSearchList();
	}
}


const mapStateToProps = (state) => {
	return {
		focused: state.get('header').get('focused'),
		list: state.get('header').get('list')
	}
}

const mapDispatchToProps = (dispatch) => {
	return {
		handleBoxFocus() {		
			dispatch(createActions.handleBoxFocusAction());
		},

		handleBoxBlur() {
			dispatch(createActions.handleBoxBlurAction());
		},
		getSearchList() {
			dispatch(createActions.getSearchListAction());
		},
		SearchMouseEnter() {
			dispatch(createActions.SearchMouseEnterAction());
		}
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(Header);
