import styled from 'styled-components';

export const HomeContainer = styled.div`
	min-width:1000px;
	width: 90%;
	margin: 30px auto;
	background:#ccc;
	overflow:hidden;
`;

export const LeftContainer = styled.div`
	float:left;
	width:65%;
	height:400px;
`;

export const RightContainer = styled.div`
	float: right;
	width:32%;
	height:400px;
	background:green;
`;

//Banner
export const BannerWrapper = styled.div`
	width:625px;
	height:270px;
	overflow:hidden;
	margin:0 auto;
	position:relative;
	.imgWrapper{
		position: absolute;
		left: 0px;
		width: 3125px;
		height: 100%;
		transition: left 1s ease-in-out;
		.bannerImg{
			width:625px;
			height:270px;
			float: left;
		}
	}
	.iconLeft{
		opacity: 0;
		position: absolute;
		top: 40%;
		width: 40px;
		height: 50px;
		font-size: 20px;
		color: #fff;
		box-shadow: 0 1px 2px rgba(0,0,0,.6);
		line-height: 50px;
		text-align: center;
		background:rgba(0,0,0,0.4);
		border-top-right-radius: 
		display: block;
		transform-origin: center center;
		transform: rotate(180deg);
	}
	.iconRight{
		opacity: 0;
		position: absolute;
		top: 40%;
		right: 0;
		width: 40px;
		height: 50px;
		font-size: 20px;
		color: #fff;
		box-shadow: 0 1px 2px rgba(0,0,0,.6);
		line-height: 50px;
		text-align: center;
		background:rgba(0,0,0,0.4);
		display: block;
	}
`;
